export function getTime(type) {
  if (type === 'start') {
    return new Date().getTime() - 3600 * 1000 * 24 * 90
  } else {
    return new Date(new Date().toDateString())
  }
}

export function debounce(func, wait, immediate) {
  let timeout, args, context, timestamp, result

  const later = function () {
    // 据上一次触发时间间隔
    const last = +new Date() - timestamp

    // 上次被包装函数被调用时间间隔last小于设定时间间隔wait
    if (last < wait && last > 0) {
      timeout = setTimeout(later, wait - last)
    } else {
      timeout = null
      // 如果设定为immediate===true，因为开始边界已经调用过了此处无需调用
      if (!immediate) {
        result = func.apply(context, args)
        if (!timeout) context = args = null
      }
    }
  }

  return function (...args) {
    context = this
    timestamp = +new Date()
    const callNow = immediate && !timeout
    // 如果延时不存在，重新设定延时
    if (!timeout) timeout = setTimeout(later, wait)
    if (callNow) {
      result = func.apply(context, args)
      context = args = null
    }

    return result
  }
}

/**
 * This is just a simple version of deep copy
 * Has a lot of edge cases bug
 * If you want to use a perfect deep copy, use lodash's _.cloneDeep
 */
export function deepClone(source) {
  if (!source && typeof source !== 'object') {
    throw new Error('error arguments', 'shallowClone')
  }
  const targetObj = source.constructor === Array ? [] : {}
  Object.keys(source).forEach(keys => {
    if (source[keys] && typeof source[keys] === 'object') {
      targetObj[keys] = deepClone(source[keys])
    } else {
      targetObj[keys] = source[keys]
    }
  })
  return targetObj
}

export function uniqueArr(arr) {
  return Array.from(new Set(arr))
}

export function isExternal(path) {
  return /^(https?:|mailto:|tel:)/.test(path)
}


export function parseURL(url) {
  var a = document.createElement('a');
  a.href = url;
  // var a = new URL(url);
  return {
    source: url,
    protocol: a.protocol.replace(':', ''),
    host: a.hostname,
    port: a.port,
    query: a.search,
    params: (function () {
      var params = {},
        seg = a.search.replace(/^\?/, '').split('&'),
        len = seg.length,
        p;
      for (var i = 0; i < len; i++) {
        if (seg[i]) {
          p = seg[i].split('=');
          params[p[0]] = p[1];
        }
      }
      return params;
    })(),
    hash: a.hash.replace('#', ''),
    path: a.pathname.replace(/^([^\/])/, '/$1')
  };
}

export function getDomain(url) {
  return document.location.protocol + "//" + document.domain
}


export function getSocialLoginUrl(thirdpart) {
  return getDomain() + '/social/login/' + thirdpart + '/'
}

export function openWindow(url, title, w, h) {
  // Fixes dual-screen position                            Most browsers       Firefox
  const dualScreenLeft = window.screenLeft !== undefined ? window.screenLeft : screen.left
  const dualScreenTop = window.screenTop !== undefined ? window.screenTop : screen.top

  const width = window.innerWidth ? window.innerWidth : document.documentElement.clientWidth ? document.documentElement.clientWidth : screen.width
  const height = window.innerHeight ? window.innerHeight : document.documentElement.clientHeight ? document.documentElement.clientHeight : screen.height

  const left = ((width / 2) - (w / 2)) + dualScreenLeft
  const top = ((height / 2) - (h / 2)) + dualScreenTop
  const newWindow = window.open(url, title, 'toolbar=no, location=no, directories=no, status=no, menubar=no, scrollbars=no, resizable=yes, copyhistory=no, width=' + w + ', height=' + h + ', top=' + top + ', left=' + left)

  // Puts focus on the newWindow
  if (window.focus) {
    newWindow.focus()
  }
}


export function getDateByTimeStr(timeStr) {
  var timeArr = timeStr.split(" ");
  var d = timeArr[0].split("-");
  var t = timeArr[1].split(":");
  return new Date(d[0], (d[1] - 1), d[2], t[0], t[1], t[2]);
}

export function timeStrAddMinStr(timestr, mins) {
  var d = getDateByTimeStr(timestr)
  var timestamp = d.getTime();
  var l = timestamp + mins * 60 * 1000
  return formatDateTime(l / 1000)
};

export function getDateTime() {
  var date = new Date();
  var y = date.getFullYear();
  var m = date.getMonth() + 1;
  m = m < 10 ? ('0' + m) : m;
  var d = date.getDate();
  d = d < 10 ? ('0' + d) : d;
  var h = date.getHours();
  h = h < 10 ? ('0' + h) : h;
  var minute = date.getMinutes();
  var second = date.getSeconds();
  minute = minute < 10 ? ('0' + minute) : minute;
  second = second < 10 ? ('0' + second) : second;
  return y + '-' + m + '-' + d + ' ' + h + ':' + minute + ':' + second;
};

export function formatDateTime(timeStamp) {
  var date = new Date();
  date.setTime(timeStamp * 1000);
  var y = date.getFullYear();
  var m = date.getMonth() + 1;
  m = m < 10 ? ('0' + m) : m;
  var d = date.getDate();
  d = d < 10 ? ('0' + d) : d;
  var h = date.getHours();
  h = h < 10 ? ('0' + h) : h;
  var minute = date.getMinutes();
  var second = date.getSeconds();
  minute = minute < 10 ? ('0' + minute) : minute;
  second = second < 10 ? ('0' + second) : second;
  return y + '-' + m + '-' + d + ' ' + h + ':' + minute + ':' + second;
};

export function isRealObj(obj) {
  var objtype = typeof obj
  if (obj == undefined || obj == null || (objtype == "string" && trim(obj) == "") || (objtype == "string" && obj == "")) {
    return false
  }
  return true;
}


export function isRealArr(arr) {
  if (arr == null || arr == undefined || arr.length <= 0) {
    return false
  }
  return true;
}

export function isInArray(arr, value) {
  if (arr == null || arr == undefined || arr.length <= 0) {
    return false
  }

  for (var i = 0; i < arr.length; i++) {
    if (value === arr[i]) {
      return true;
    }
  }
  return false;
}

export function isInArrayNum(arr, value) {
  if (arr == null || arr == undefined || arr.length <= 0) {
    return -1
  }
  for (var i = 0; i < arr.length; i++) {
    if (value === arr[i]) {
      return i;
    }
  }
  return -1;
}

export function getNowFormatDate() {
  var date = new Date();
  var seperator1 = "-";
  var year = date.getFullYear();
  var month = date.getMonth() + 1;
  var strDate = date.getDate();
  if (month >= 1 && month <= 9) {
    month = "0" + month;
  }
  if (strDate >= 0 && strDate <= 9) {
    strDate = "0" + strDate;
  }
  var currentdate = year + seperator1 + month + seperator1 + strDate;
  return currentdate;
}

export function trancateStr(str, count) {
  if (str != null && str.length > count) {
    return str.substring(0, count)
  } else {
    return str
  }
}


export function optionUtil(key, options) {
  for (var i = 0; i < options.length; i++) {
    if (options[i][0] === key) {
      return options[i][1]
    }
  }
  return undefined
}

export function removeFromList(json, list, isDeleteEmpty) {
  var tmpJson = {}
  for (var key in json) {
    if (json[key] === null) {
      continue
    }
    var str = json[key] + ""
    if (!isInArray(list, key)) {
      if (isDeleteEmpty && str != "") {
        tmpJson[key] = json[key]
      }
    }
  }
  return tmpJson
}

export function conver2KV(kvNameArr, options) {
  var ret = []
  if (kvNameArr == undefined || kvNameArr.length <= 0) {
    kvNameArr = ['value', 'label']
  }
  for (var i = 0; i < options.length; i++) {
    var d = {}
    d[kvNameArr[0]] = options[i][0]
    d[kvNameArr[1]] = options[i][1]
    ret.push(d)
  }
  return ret
}

//去左空格;
export function ltrim(s) {
  return s.replace(/(^\s*)/g, "");
}

//去右空格;
export function rtrim(s) {
  return s.replace(/(\s*$)/g, "");
}

//去左右空格;
export function trim(s) {
  return s.replace(/(^\s*)|(\s*$)/g, "");
}


import axios from 'axios'

export function axiosPro(method, url, data, params, headers, responseType) {
  return new Promise(function (resolve, reject) {
    axios({
      method: method,
      url: url,
      data: data,
      params: params,
      headers: headers,
      responseType: responseType
    }).then(function (ret) {
      resolve(ret);
    }).catch(function (err) {
      reject(err);
    })
  })
};

export function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

export function getAttrsBySpecC(arr, attrname, spec_character) {
  var attrstr = ""
  for (var i = 0; i < arr.length; i++) {
    if (arr[i].hasOwnProperty(attrname)) {
      if (attrstr == "") {
        attrstr = arr[i][attrname]
      } else {
        attrstr = attrstr + spec_character + arr[i][attrname]
      }
    }
  }
  return attrstr
}


