export function getDataWithUnit(data, unit, unitarr) {
  data = parseFloat(data)
  var um = data
  if (data >= (unit * unit * unit * unit)) {
    um = (data / (unit * unit * unit * unit)).toFixed(2) + "" + unitarr[4]
  } else if (data >= (unit * unit * unit)) {
    um = (data / (unit * unit * unit)).toFixed(2) + "" + unitarr[3]
  } else if (data >= (unit * unit)) {
    um = (data / (unit * unit)).toFixed(2) + "" + unitarr[2]

    console.log(data/(unit*unit))

  } else if (data >= unit) {
    um = (data / unit).toFixed(2) + "" + unitarr[1]
  } else {
    um = data.toFixed(2) + "" + unitarr[0]
  }
  return um
}


